import React, { useState } from "react";
import { Link } from "react-router-dom";

import './Join.css';

function Join() {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');

    return (
        <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join</h1>
                <div><input placeholder="Name" className="joinInput" type="text" value={name} onChange={(event) => setName(event.target.value)} /></div>
                <div><input placeholder="Room" className="joinInput mt-20" type="text" value={room} onChange={(event) => setRoom(event.target.value)} /></div>
                <Link to={`/chat?name=${encodeURIComponent(name)}&room=${encodeURIComponent(room)}`}>
                    <button className={'button mt-20'} type="button" onClick={() => {
                        if (!name.trim() || !room.trim()) {
                            console.log('Name and room are required');
                            return;
                        }
                    }}>Sign In</button>
                </Link>
            </div>
        </div>
    )
}

export default Join;
