import React, { useState, useMemo } from "react";
import { Link } from "react-router-dom";
import "./Join.css";

function Join() {
  const [user_name, set_user_name] = useState("");
  const [user_room, set_user_room] = useState("");

  // quick alias generator to avoid real names (sender anonymity UX)
  const randomAlias = useMemo(() => () => {
    const animals = ["Falcon","Kestrel","Panther","Lynx","Wolf","Orca","Viper","Raven","Jaguar","Otter","Fox","Puma","Eagle","Mamba","Ibis","Iguana","Heron","Drake","Bison","Ibex"];
    const adj = ["Silent","Hidden","Shadow","Ghost","Misty","Azure","Crimson","Obsidian","Golden","Silver","Ivory","Emerald","Cobalt","Amber","Violet","Indigo"];
    const pick = (arr) => arr[Math.floor(Math.random()*arr.length)];
    return `${pick(adj)}${pick(animals)}${Math.floor(100+Math.random()*900)}`;
  }, []);

  const canJoin = user_name.trim().length > 0 && user_room.trim().length > 0;

  const onNameChange = (e) => set_user_name(e.target.value.replace(/\s+/g, " ").trimStart());
  const onRoomChange = (e) => set_user_room(e.target.value.replace(/\s+/g, "-").toLowerCase());

  const handleAlias = () => set_user_name(randomAlias());

  // keep Link-based navigation for compatibility with your Chat.js (query-string parsing)
  const joinHref = `/chat?user_name=${encodeURIComponent(user_name.trim())}&user_room=${encodeURIComponent(user_room.trim())}`;

  const onKeyDown = (e) => {
    if (e.key === "Enter" && !canJoin) e.preventDefault();
  };

  return (
    <div className="joinOuterContainer">
      <div className="joinInnerContainer">
        <h1 className="heading">Join</h1>

        <div>
          <input
            placeholder="Alias (recommended: do not use real name)"
            className="joinInput"
            type="text"
            value={user_name}
            onChange={onNameChange}
            onKeyDown={onKeyDown}
            maxLength={48}
            autoComplete="off"
          />
          <div className="row mt-12">
            <button type="button" className="button secondaryBtn" onClick={handleAlias}>
              Random Alias
            </button>
          </div>
        </div>

        <div className="mt-20">
          <input
            placeholder="Room (e.g., project-x)"
            className="joinInput"
            type="text"
            value={user_room}
            onChange={onRoomChange}
            onKeyDown={onKeyDown}
            maxLength={64}
            autoComplete="off"
          />
        </div>

        <Link
          to={joinHref}
          onClick={(event) => { if (!canJoin) event.preventDefault(); }}
          className="mt-20"
        >
          <button className="button" type="submit" disabled={!canJoin}>
            Sign In
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Join;
