import React, { useState } from "react";
import "./Message.css";

const Message = ({ message, current_user_name }) => {
  const { message_id, message_text, message_sender } = message;
  const [isEditing, setIsEditing] = useState(false);
  const [editedMessage, setEditedMessage] = useState(message_text);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    // Handle saving edited message (e.g., send API request)
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    setEditedMessage(e.target.value);
  };

  const text_to_display = `id: ${message_id} | text: ${message_text}`;

  return (
    <div className={message_sender === current_user_name ? "messageContainer justifyEnd" : "messageContainer justifyStart"}>
      <div className={message_sender === current_user_name ? "messageBox backgroundBlue" : "messageBox backgroundLight"}>
        {isEditing ? (
          <textarea
            className="messageText"
            value={editedMessage}
            onChange={handleInputChange}
          />
        ) : (
          <p className="messageText colorWhite">{text_to_display}</p>
        )}
        {message_sender === current_user_name && (
          <button onClick={isEditing ? handleSaveClick : handleEditClick}>
            {isEditing ? "Save" : "Edit"}
          </button>
        )}
      </div>
      <p className={message_sender === current_user_name ? "sentText pr-10" : "sentText pl-10"}>
        {message_sender}
      </p>
    </div>
  );
};

export default Message;
